import { useState, useEffect } from "react";
import "./App.css";
import Note from "./Note";

function App() {
  // -- Backend-related state --
  const [loading, setLoading] = useState(true);
  const [notes, setNotes] = useState(undefined);

  const entry = {
    _id: "5f6e3d9d2e3d2f2f2f2f2f2f",
    title: "This is a note",
    content: "This is the content of the note",
  };

  const editNote = () => {
    console.log("edit note");
  };

  const deleteNote = () => {
    console.log("delete note");
  };

  // -- Database interaction functions --
  useEffect(() => {
    const getNotes = async () => {
      try {
        await fetch("http://localhost:4000/getAllNotes").then(
          async (response) => {
            if (!response.ok) {
              console.log("Served failed:", response.status);
            } else {
              await response.json().then((data) => {
                getNoteState(data.response);
              });
            }
          }
        );
      } catch (error) {
        console.log("Fetch function failed:", error);
      } finally {
        setLoading(false);
      }
    };

    getNotes();
  }, []);

  // -- State modification functions --
  const getNoteState = (data) => {
    setNotes(data);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1 style={AppStyle.title}>QuirkNotes</h1>
        <h4 style={AppStyle.text}>The best note-taking app ever </h4>

        <div style={AppStyle.notesSection}>
          {loading ? (
            <>Loading...</>
          ) : notes ? (
            notes.map((entry) => {
              return (
                <div key={entry._id}>
                  <Note
                    entry={entry}
                    editNote={editNote}
                    deleteNote={deleteNote}
                  />
                </div>
              );
            })
          ) : (
            <div style={AppStyle.notesError}>
              Something has gone horribly wrong! We can't get the notes!
            </div>
          )}
        </div>
      </header>
    </div>
  );
}

const AppStyle = {
  dimBackground: {
    opacity: "20%",
    pointerEvents: "none",
  },
  notesSection: {
    display: "flex",
    flexWrap: "wrap",
    justifyContent: "center",
  },
};

export default App;
