import { useState } from "react";
import "./App.css";
import Note from "./Note";

function App() {
  // -- Backend-related state --
  const [loading, setLoading] = useState(true);

  const entry = {
    _id: "5f6e3d9d2e3d2f2f2f2f2f2f",
    title: "This is a note",
    content: "This is the content of the note",
  };

  const editNote = () => {
    console.log("edit note");
  };

  const deleteNote = () => {
    console.log("delete note");
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1 style={AppStyle.title}>QuirkNotes</h1>
        <h4 style={AppStyle.text}>The best note-taking app ever </h4>

        <div style={AppStyle.notesSection}>
          {loading ? (
            <>Loading...</>
          ) : (
            <Note entry={entry} editNote={editNote} deleteNote={deleteNote} />
          )}
        </div>
      </header>
    </div>
  );
}

const AppStyle = {
  dimBackground: {
    opacity: "20%",
    pointerEvents: "none",
  },
  notesSection: {
    display: "flex",
    flexWrap: "wrap",
    justifyContent: "center",
  },
};

export default App;
